export function deleteSelectedShapes(
  shapes: Shape[],
  selectedShapeIndex: number
) {
  if (selectedShapeIndex < 0 || selectedShapeIndex > shapes.length) return;
  shapes.splice(selectedShapeIndex, 1);
  console.log(shapes);
}

import { Shape } from "../Shapes/Shape";

export function exportSelectedShape(
  ctx: CanvasRenderingContext2D,
  selectedShape: Shape
) {
  const exportedImage = document.getElementById(
    "exportedImage"
  ) as HTMLImageElement;
  const tempCanvas = document.createElement("canvas") as HTMLCanvasElement;
  const tempContext = tempCanvas.getContext("2d")!;
  tempCanvas.width = ctx.canvas.width;
  tempCanvas.height = ctx.canvas.height;
  selectedShape.isSelected = false;
  selectedShape.draw(tempContext);
  const imageData = tempCanvas.toDataURL("image/png");
  exportedImage.src = imageData;
  const downloadLink = document.createElement("a");
  downloadLink.href = imageData;
  downloadLink.download = "selected_shapes.png";
  document.body.appendChild(downloadLink);
  downloadLink.click();
  document.body.removeChild(downloadLink);
}

import { Shape } from "../Shapes/Shape";

export function sendToBack(shapes: Shape[], selectedShapeIndex: number) {
  if (selectedShapeIndex <= 0) return;
  const [selectedShape] = shapes.splice(selectedShapeIndex, 1);
  shapes.unshift(selectedShape);
}

export function sendBackward(shapes: Shape[], selectedShapeIndex: number) {
  if (selectedShapeIndex <= 0) return;
  const selectedShape = shapes[selectedShapeIndex];
  shapes[selectedShapeIndex] = shapes[selectedShapeIndex - 1];
  shapes[selectedShapeIndex - 1] = selectedShape;
}

export function bringForward(shapes: Shape[], selectedShapeIndex: number) {
  if (selectedShapeIndex >= (shapes.length - 1)) return;
  const selectedShape = shapes[selectedShapeIndex];
  shapes[selectedShapeIndex] = shapes[selectedShapeIndex + 1];
  shapes[selectedShapeIndex + 1] = selectedShape;
}



export function bringToFront(shapes: Shape[], selectedShapeIndex: number) {
    if (selectedShapeIndex >= (shapes.length - 1)) return;
    const [selectedShape] = shapes.splice(selectedShapeIndex, 1);
    shapes.push(selectedShape);
}

import { Shape } from "../Shapes/Shape";

/**Shortcut section */
export function handleShortcut() {
  const shortcutItemsEl = document.querySelector(
    "#shortcut_items"
  ) as HTMLDivElement;
  shortcutItemsEl.style.display = "none";
  let flag = false;
  const shortcutEl = document.querySelector("#shortcut") as HTMLButtonElement;
  shortcutEl.addEventListener("click", () => {
    if (flag) {
      shortcutItemsEl.style.display = "none";
      flag = false;
    } else {
      shortcutItemsEl.style.display = "block";
      flag = true;
    }
  });
}

export function toggleSidePanel(isShowingSidePanel: boolean) {
  const sidePane = document.querySelector(".panelColumn") as HTMLDivElement;
  if (isShowingSidePanel) {
    sidePane.style.display = "block";
  } else {
    sidePane.style.display = "none";
  }
}

export function activateRedoUndoBtn(
  undoStack: Shape[][],
  redoStack: Shape[][]
) {
  const undoBtn = document.querySelector("#undoBtn") as HTMLButtonElement;
  const redoBtn = document.querySelector("#redoBtn") as HTMLButtonElement;
  if (undoStack.length === 0) {
    undoBtn.style.opacity = "0.4";
    undoBtn.disabled = true;
  } else {
    undoBtn.style.opacity = "1";
    undoBtn.disabled = false;
  }
  if (redoStack.length === 0) {
    redoBtn.style.opacity = "0.4";
    redoBtn.disabled = true;
  } else {
    redoBtn.style.opacity = "1";
    redoBtn.disabled = false;
  }
}

import { Shape } from "../Shapes/Shape";

export function saveCurrentState(shapes: Shape[], undoStack: Shape[][]) {
  undoStack.push([...shapes]);
}

export function savePreviousState(shapes: Shape[], redoStack: Shape[][]) {
  redoStack.push([...shapes]);
}

export function undo(
  shapes: Shape[],
  undoStack: Shape[][],
  redoStack: Shape[][]
) {
  if (undoStack.length > 0) {
    savePreviousState(shapes, redoStack);
    const previousState = undoStack.pop();
    if (previousState) {
      shapes.length = 0;
      shapes.push(...previousState);
    }
  }
}

export function redo(
  shapes: Shape[],
  undoStack: Shape[][],
  redoStack: Shape[][]
) {
  if (redoStack.length > 0) {
    saveCurrentState(shapes, undoStack);
    const nextState = redoStack.pop();
    if (nextState) {
      shapes.length = 0;
      shapes.push(...nextState);
    }
  }
}

export function clearRedoStack(redoStack: Shape[][]) {
  redoStack.length = 0;
}

export const DIMENSION = {
    CANVAS_WIDTH: window.innerWidth,
    CANVAS_HEIGHT: window.innerHeight,
}

export const EDGE_DETECTION_WIDTH = 10;
export const SELECTED_SHAPE_INDICATOR_CIRCLE_RADIUS = 5;
export const THIN_LINE_WIDTH = 1;
export const DRAW_THIN_LINE_WIDTH = 3;
export const BOLD_LINE_WIDTH = 3;
export const DRAW_BOLD_LINE_WIDTH = 9;
export const EXTRA_BOLD_LINE_WIDTH = 5;
export const DRAW_EXTRA_BOLD_LINE_WIDTH = 15;
export const DASH_WIDTH = 10;
export const DASH_GAP  = 15;
export const DOT_WIDTH = 2;
export const DOT_GAP = 6;
export const ARROW_HEAD_LENGTH = 15;

import {
  ARROW_HEAD_LENGTH,
  EDGE_DETECTION_WIDTH,
  SELECTED_SHAPE_INDICATOR_CIRCLE_RADIUS,
} from "../Constants/Constants";
import { IPoint, SHAPES, selectionIndicateCircle } from "../Utils/Common";
import { Shape } from "./Shape";

export class ArrowLine extends Shape {
  private _end: IPoint;

  constructor(
    start: IPoint,
    end: IPoint,
    fillColor = "transparent",
    strokeColor = "black",
    strokeWidth = 2,
    strokeStyle: number[] = []
  ) {
    super(
      start,
      SHAPES.ARROW,
      fillColor,
      strokeColor,
      strokeWidth,
      strokeStyle
    );
    this._end = end;
  }

  public get end(): IPoint {
    return this._end;
  }
  public set end(value: IPoint) {
    this._end = value;
  }

  draw(ctx: CanvasRenderingContext2D) {
    const headLength = ARROW_HEAD_LENGTH;
    const dx = this.end.posX - this.position.posX;
    const dy = this.end.posY - this.position.posY;
    const angle = Math.atan2(dy, dx);
    ctx.save();
    ctx.beginPath();
    ctx.moveTo(this.position.posX, this.position.posY);
    ctx.lineTo(this.end.posX, this.end.posY);
    ctx.lineTo(
      this.end.posX - headLength * Math.cos(angle - Math.PI / 6),
      this.end.posY - headLength * Math.sin(angle - Math.PI / 6)
    );
    ctx.moveTo(this.end.posX, this.end.posY);
    ctx.lineTo(
      this.end.posX - headLength * Math.cos(angle + Math.PI / 6),
      this.end.posY - headLength * Math.sin(angle + Math.PI / 6)
    );
    ctx.lineWidth = this.strokeWidth;
    ctx.setLineDash(this.strokeStyle);
    ctx.strokeStyle = this.strokeColor;
    ctx.stroke();
    ctx.restore();
    if (this.isSelected) {
      this.drawOutline(ctx);
    }
  }

  drawOutline(ctx: CanvasRenderingContext2D): void {
    selectionIndicateCircle(
      ctx,
      this.position,
      SELECTED_SHAPE_INDICATOR_CIRCLE_RADIUS
    );
    selectionIndicateCircle(
      ctx,
      this.end,
      SELECTED_SHAPE_INDICATOR_CIRCLE_RADIUS
    );
  }

  isMouseWithinShape(point: IPoint): boolean {
    const distanceToPoint = this.distanceFromPointToLineSegment(
      point,
      this.position,
      this.end
    );
    const lineThickness = 5;
    const headLength = 10;

    if (distanceToPoint <= lineThickness) {
      return true;
    }

    const dx = this.end.posX - this.position.posX;
    const dy = this.end.posY - this.position.posY;
    const angle = Math.atan2(dy, dx);

    const arrowTip1 = {
      posX: this.end.posX - headLength * Math.cos(angle - Math.PI / 6),
      posY: this.end.posY - headLength * Math.sin(angle - Math.PI / 6),
    };
    const arrowTip2 = {
      posX: this.end.posX - headLength * Math.cos(angle + Math.PI / 6),
      posY: this.end.posY - headLength * Math.sin(angle + Math.PI / 6),
    };

    const distanceToArrowTip1 = this.distanceFromPointToLineSegment(
      point,
      this.end,
      arrowTip1
    );
    const distanceToArrowTip2 = this.distanceFromPointToLineSegment(
      point,
      this.end,
      arrowTip2
    );

    return (
      distanceToArrowTip1 <= lineThickness ||
      distanceToArrowTip2 <= lineThickness
    );
  }

  move(dx: number, dy: number): void {
    this.position.posX += dx;
    this.position.posY += dy;
    this.end.posX += dx;
    this.end.posY += dy;
  }

  private distanceFromPointToLineSegment(
    point: IPoint,
    start: IPoint,
    end: IPoint
  ): number {
    const A = point.posX - start.posX;
    const B = point.posY - start.posY;
    const C = end.posX - start.posX;
    const D = end.posY - start.posY;

    const dot = A * C + B * D;
    const len_sq = C * C + D * D;
    let param = dot / len_sq;

    let xx, yy;
    if (param < 0 || (start.posX === end.posX && start.posY === end.posY)) {
      xx = start.posX;
      yy = start.posY;
    } else if (param > 1) {
      xx = end.posX;
      yy = end.posY;
    } else {
      xx = start.posX + param * C;
      yy = start.posY + param * D;
    }

    const dx = point.posX - xx;
    const dy = point.posY - yy;
    return Math.sqrt(dx * dx + dy * dy);
  }

  isMouseNearEdge(currentMousePosition: IPoint): string | boolean | null {
    if (
      Math.abs(currentMousePosition.posX - this.position.posX) <=
        EDGE_DETECTION_WIDTH ||
      Math.abs(currentMousePosition.posY - this.position.posY) <=
        EDGE_DETECTION_WIDTH
    )
      return "start";
    if (
      Math.abs(currentMousePosition.posX - this.end.posX) <=
        EDGE_DETECTION_WIDTH ||
      Math.abs(currentMousePosition.posY - this.end.posY) <=
        EDGE_DETECTION_WIDTH
    )
      return "end";
    return null;
  }

  reSize(...args: any[]): void {
    const [edge, currentMousePosition] = args;
    switch (edge) {
      case "start":
        const dx = currentMousePosition.posX - this.position.posX;
        const dy = currentMousePosition.posY - this.position.posY;
        this.position.posX += dx;
        this.position.posY += dy;
        break;
      case "end":
        const edx = currentMousePosition.posX - this.end.posX;
        const edy = currentMousePosition.posY - this.end.posY;
        this.end.posX += edx;
        this.end.posY += edy;
        break;
    }
  }
  static generateArrowLine(
    start: IPoint,
    end: IPoint,
    fillColor: string,
    strokeColor: string,
    strokeWidth: number,
    strokeStyle: number[]
  ) {
    return new ArrowLine(
      start,
      end,
      fillColor,
      strokeColor,
      strokeWidth,
      strokeStyle
    );
  }
}

import {
  EDGE_DETECTION_WIDTH,
  SELECTED_SHAPE_INDICATOR_CIRCLE_RADIUS,
} from "../Constants/Constants";
import {
  IDimension,
  IPoint,
  SHAPES,
  getLeftBottomCircleCenter,
  getLeftTopCircleCenter,
  getRightBottomCircleCenter,
  getRightTopCircleCenter,
  selectionIndicateCircle,
  selectionIndicateRectangle,
} from "../Utils/Common";
import { Shape } from "./Shape";

export class Circle extends Shape {
  radius: number;

  constructor(
    position: IPoint,
    radius: number,
    fillColor = "transparent",
    strokeColor = "black",
    strokeWidth = 2,
    strokeStyle: number[] = []
  ) {
    super(
      position,
      SHAPES.CIRCLE,
      fillColor,
      strokeColor,
      strokeWidth,
      strokeStyle
    );
    this.radius = radius;
  }

  draw(ctx: CanvasRenderingContext2D) {

    ctx.save();
    ctx.beginPath();
    ctx.arc(
      this.position.posX,
      this.position.posY,
      this.radius,
      0,
      Math.PI * 2
    );
    ctx.fillStyle = this.fillColor;
    ctx.fill();
    ctx.lineWidth = this.strokeWidth;
    ctx.setLineDash(this.strokeStyle);
    ctx.strokeStyle = this.strokeColor;
    ctx.stroke();
    ctx.restore();
    if (this.isSelected) {
      this.drawOutline(ctx);
    }
  }

  getCenter(): IPoint {
    return this.position;
  }

  drawOutline(ctx: CanvasRenderingContext2D): void {
    const position: IPoint = {
      posX: this.position.posX - this.radius,
      posY: this.position.posY - this.radius,
    };
    const dimension: IDimension = {
      width: this.radius * 2,
      height: this.radius * 2,
    };

    selectionIndicateRectangle(ctx, position, dimension);
    const leftTopCircleCenter: IPoint = getLeftTopCircleCenter(position);
    const leftBottomCircle: IPoint = getLeftBottomCircleCenter(
      position,
      dimension
    );
    const rightTopCircle: IPoint = getRightTopCircleCenter(position, dimension);
    const rightBottomCircle: IPoint = getRightBottomCircleCenter(
      position,
      dimension
    );
    selectionIndicateCircle(
      ctx,
      leftTopCircleCenter,
      SELECTED_SHAPE_INDICATOR_CIRCLE_RADIUS
    );
    selectionIndicateCircle(
      ctx,
      leftBottomCircle,
      SELECTED_SHAPE_INDICATOR_CIRCLE_RADIUS
    );
    selectionIndicateCircle(
      ctx,
      rightTopCircle,
      SELECTED_SHAPE_INDICATOR_CIRCLE_RADIUS
    );
    selectionIndicateCircle(
      ctx,
      rightBottomCircle,
      SELECTED_SHAPE_INDICATOR_CIRCLE_RADIUS
    );
  }

  isMouseWithinShape(point: IPoint): boolean {
    const dx = point.posX - this.position.posX;
    const dy = point.posY - this.position.posY;
    const distance = Math.sqrt(dx * dx + dy * dy);
    return distance <= this.radius;
  }

  isMouseNearEdge(currentMousePosition: IPoint): string | null {
    const rectLeft = this.position.posX - this.radius;
    const rectRight = this.position.posX + this.radius;
    const rectTop = this.position.posY - this.radius;
    const rectBottom = this.position.posY + this.radius;

    const xNearLeft =
      Math.abs(currentMousePosition.posX - rectLeft) <= EDGE_DETECTION_WIDTH;
    const xNearRight =
      Math.abs(currentMousePosition.posX - rectRight) <= EDGE_DETECTION_WIDTH;
    const yNearTop =
      Math.abs(currentMousePosition.posY - rectTop) <= EDGE_DETECTION_WIDTH;
    const yNearBottom =
      Math.abs(currentMousePosition.posY - rectBottom) <= EDGE_DETECTION_WIDTH;

    if (xNearLeft && yNearTop) return "top-left";
    if (xNearRight && yNearTop) return "top-right";
    if (xNearLeft && yNearBottom) return "bottom-left";
    if (xNearRight && yNearBottom) return "bottom-right";
    if (xNearLeft) return "left";
    if (xNearRight) return "right";
    if (yNearTop) return "top";
    if (yNearBottom) return "bottom";

    return null;
  }

  reSize(...args: any): void {
    const [currentMousePosition] = args;
    const x = currentMousePosition.posX - this.position.posX;
    const y = currentMousePosition.posY - this.position.posY;
    this.radius = Math.sqrt(x * x + y * y);
  }

  static generateCircle(
    position: IPoint,
    radius: number,
    fillColor: string,
    strokeColor: string,
    strokeWidth: number,
    strokeStyle: number[]
  ): Circle {
    return new Circle(
      position,
      radius,
      fillColor,
      strokeColor,
      strokeWidth,
      strokeStyle
    );
  }

  move(dx: number, dy: number): void {
    this.position.posX += dx;
    this.position.posY += dy;
  }
}

import { IPoint, SHAPES } from "../Utils/Common";
import { Shape } from "./Shape";

export class Draw extends Shape {
  path: IPoint[];

  constructor(start: IPoint, strokeColor = "black", strokeWidth = 16) {
    super(start, SHAPES.DRAW, "", strokeColor, strokeWidth, []);
    this.path = [start];
    this.strokeColor = strokeColor;
    this.strokeWidth = strokeWidth;
  }

  addPoint(point: IPoint) {
    this.path.push(point);
  }


  draw(ctx: CanvasRenderingContext2D): void {
    if (this.path.length < 2) return;
    ctx.save();
    ctx.beginPath();
    ctx.moveTo(this.path[0].posX, this.path[0].posY);
    for (let i = 1; i < this.path.length; i++) {
      ctx.lineTo(this.path[i].posX, this.path[i].posY);
    }
    ctx.lineWidth = this.strokeWidth;
    ctx.strokeStyle = this.strokeColor;
    ctx.stroke();
    ctx.restore();
  }

  isMouseWithinShape(currentMousePosition: IPoint): boolean {
    const tempCanvas = document.createElement("canvas");
    const tempContext = tempCanvas.getContext("2d")!;
    tempCanvas.width = 1;
    tempCanvas.height = 1;

    tempContext.beginPath();
    tempContext.moveTo(this.path[0].posX, this.path[0].posY);
    for (let i = 1; i < this.path.length; i++) {
      tempContext.lineTo(this.path[i].posX, this.path[i].posY);
    }
    tempContext.lineWidth = this.strokeWidth;
    tempContext.strokeStyle = this.strokeColor;
    tempContext.stroke();

    return tempContext.isPointInStroke(
      currentMousePosition.posX,
      currentMousePosition.posY
    );
  }
  // @ts-ignore
  isMouseNearEdge(currentMousePosition: IPoint): string | boolean | null {
    return null;
  }
  // @ts-ignore
  move(dx: number, dy: number): void {}
  // @ts-ignore
  reSize(...args: any): void {}
  //@ts-ignore
  drawOutline(ctx: CanvasRenderingContext2D): void {}
  //@ts-ignore
  setIsSelected(value: boolean): void {}
}

