import { IPoint, SHAPES } from "../Utils/Common";

export abstract class Shape {
  private _position: IPoint;
  private _shapeType: SHAPES;
  private _isSelected: boolean = false;
  private _fillColor: string;
  private _strokeColor: string;
  private _strokeWidth: number;
  private _strokeStyle: number[];

  constructor(
    position: IPoint,
    shapeType: SHAPES,
    fillColor: string,
    strokeColor: string,
    strokeWidth: number,
    strokeStyle: number[]
  ) {
    this._position = position;
    this._shapeType = shapeType;
    this._fillColor = fillColor;
    this._strokeColor = strokeColor;
    this._strokeWidth = strokeWidth;
    this._strokeStyle = strokeStyle;
  }

  abstract draw(ctx: CanvasRenderingContext2D): void;
  abstract isMouseWithinShape(currentMousePosition: IPoint): boolean;
  abstract move(dx: number, dy: number): void;
  abstract isMouseNearEdge(
    currentMousePosition: IPoint
  ): string | null | boolean;
  abstract reSize(...args: any): void;
  abstract drawOutline(ctx: CanvasRenderingContext2D): void;


  public get strokeStyle(): number[] {
    return this._strokeStyle;
  }
  public set strokeStyle(value: number[]) {
    this._strokeStyle = value;
  }
  public get strokeWidth(): number {
    return this._strokeWidth;
  }
  public set strokeWidth(value: number) {
    this._strokeWidth = value;
  }
  public get strokeColor(): string {
    return this._strokeColor;
  }
  public set strokeColor(value: string) {
    this._strokeColor = value;
  }
  public get fillColor(): string {
    return this._fillColor;
  }
  public set fillColor(value: string) {
    this._fillColor = value;
  }
  public get shapeType(): SHAPES {
    return this._shapeType;
  }
  public set shapeType(value: SHAPES) {
    this._shapeType = value;
  }
  public get position(): IPoint {
    return this._position;
  }
  public set position(value: IPoint) {
    this._position = value;
  }

  public get isSelected(): boolean {
    return this._isSelected;
  }
  public set isSelected(value: boolean) {
    this._isSelected = value;
  }
}

import { SELECTED_SHAPE_INDICATOR_CIRCLE_RADIUS } from "../Constants/Constants";
import {
  IDimension,
  IPoint,
  SHAPES,
  getLeftBottomCircleCenter,
  getLeftTopCircleCenter,
  getRightBottomCircleCenter,
  getRightTopCircleCenter,
  selectionIndicateCircle,
  selectionIndicateRectangle,
} from "../Utils/Common";
import { Shape } from "./Shape";

export class Text extends Shape {
  text: string;
  boundingBox: { x: number; y: number; width: number; height: number };

  constructor(
    position: IPoint,
    text: string,
    boundingBox: { x: number; y: number; width: number; height: number },
    fontColor: string = "black"
  ) {
    super(
      position, 
      SHAPES.TEXT,
      "",
      fontColor,
      1,
      []
    );
    this.text = text;
    this.boundingBox = boundingBox;
  }

  draw(ctx: CanvasRenderingContext2D) {
    ctx.font = "24px Virgil";
    ctx.save();
    ctx.fillStyle = "white";
    ctx.fillRect(
      this.boundingBox.x - 5,
      this.boundingBox.y + 14,
      this.boundingBox.width + 10,
      this.boundingBox.height + 14
    )
    ctx.restore();
    ctx.fillStyle = this.strokeColor;
    ctx.fillText(this.text, this.position.posX, this.position.posY + 18);
    if (this.isSelected) {
      this.drawOutline(ctx);
    }
  }

  drawOutline(ctx: CanvasRenderingContext2D): void {
    const position: IPoint = {
      posX: this.boundingBox.x - 5,
      posY: this.boundingBox.y + 14,
    };
    const dimension: IDimension = {
      width: this.boundingBox.width + 10,
      height: this.boundingBox.height + 14,
    };

    selectionIndicateRectangle(ctx, position, dimension);

    const leftTopCircleCenter: IPoint = getLeftTopCircleCenter(position);
    const leftBottomCircle: IPoint = getLeftBottomCircleCenter(
      position,
      dimension
    );
    const rightTopCircle: IPoint = getRightTopCircleCenter(position, dimension);
    const rightBottomCircle: IPoint = getRightBottomCircleCenter(
      position,
      dimension
    );
    selectionIndicateCircle(
      ctx,
      leftTopCircleCenter,
      SELECTED_SHAPE_INDICATOR_CIRCLE_RADIUS
    );
    selectionIndicateCircle(
      ctx,
      leftBottomCircle,
      SELECTED_SHAPE_INDICATOR_CIRCLE_RADIUS
    );
    selectionIndicateCircle(
      ctx,
      rightTopCircle,
      SELECTED_SHAPE_INDICATOR_CIRCLE_RADIUS
    );
    selectionIndicateCircle(
      ctx,
      rightBottomCircle,
      SELECTED_SHAPE_INDICATOR_CIRCLE_RADIUS
    );
  }

  setIsSelected(value: boolean): void {
    this.isSelected = value;
  }

  isMouseWithinShape(point: IPoint): boolean {
    return (
      point.posX >= this.boundingBox.x &&
      point.posX <= this.boundingBox.x + this.boundingBox.width &&
      point.posY >= this.boundingBox.y + 16 &&
      point.posY <= this.boundingBox.y + 16 + this.boundingBox.height
    );
  }

  move(dx: number, dy: number): void {
    this.position.posX += dx;
    this.position.posY += dy;
    this.boundingBox.x += dx;
    this.boundingBox.y += dy;
  }
  // @ts-ignore
  isMouseNearEdge(currentMousePosition: IPoint): string | boolean | null {
    return null;
  }
  // @ts-ignore
  reSize(...args: any): void {}
}

import { ArrowLine } from "../Shapes/ArrowLine";
import { Circle } from "../Shapes/Circle";
import { Draw } from "../Shapes/Draw";
import { Line } from "../Shapes/Line";
import { Rectangle } from "../Shapes/Rectangle";
import { Shape } from "../Shapes/Shape";
import { Text } from "../Shapes/Text";
import { IPoint, SHAPES } from "../Utils/Common";

export function getDataFromLocalStorage(): Shape[] | null {

  const savedData = localStorage.getItem("savedData");
  if (savedData) {
    const shapes:any[] = JSON.parse(savedData);
    const savedShapes: any[] = [];
    shapes.forEach((shape) => {
      if (shape._shapeType === SHAPES.RECTANGLE) {
        savedShapes.push(
          new Rectangle(
            shape._position,
            shape.dimension,
            shape._fillColor,
            shape._strokeColor,
            shape._strokeWidth,
            shape._strokeStyle
          )
        );
      } else if (shape._shapeType === SHAPES.CIRCLE) {
        savedShapes.push(
          new Circle(
            shape._position,
            shape.radius,
            shape._fillColor,
            shape._strokeColor,
            shape._strokeWidth,
            shape._strokeStyle
          )
        );
      } else if (shape._shapeType === SHAPES.LINE) {
        savedShapes.push(
          new Line(
            shape._position,
            shape.end,
            shape._fillColor,
            shape._strokeColor,
            shape._strokeWidth,
            shape._strokeStyle
          )
        );
      } else if (
        shape._shapeType === SHAPES.ARROW 
      ) {
        savedShapes.push(
          new ArrowLine(
            shape._position,
            shape.end,
            shape._fillColor,
            shape._strokeColor,
            shape._strokeWidth,
            shape._strokeStyle
          )
        );
      } else if (shape._shapeType === SHAPES.TEXT) {
        savedShapes.push(
          new Text(
            shape._position,
            shape.text,
            shape.boundingBox,
            shape._strokeColor
          )
        );
      } else if (shape._shapeType === SHAPES.DRAW) {
        const newDraw = new Draw(
          shape._position,
          shape._strokeColor,
          shape._strokeWidth
        );
        shape.path.forEach((path: IPoint) => newDraw.addPoint(path));
        savedShapes.push(newDraw);
      }
    });
    return savedShapes as Shape[];
  }
  return null;
}

import { body, cursorBtn, removeBg } from "./HighlightSelectedTool";

export interface IPoint {
  posX: number;
  posY: number;
}

export interface IDimension {
  width: number;
  height: number;
}

export enum SHAPES {
  RECTANGLE,
  CIRCLE,
  LINE,
  ARROW,
  TEXT,
  ERASER,
  NONE,
  CURSOR,
  DRAW,
}

export function adjustToolSection() {
  removeBg();
  cursorBtn.style.backgroundColor = "#E0DFFF";
  body.style.cursor = "default";
}

export function selectionIndicateCircle(
  ctx: CanvasRenderingContext2D,
  center: IPoint,
  radius: number
) {
  ctx.save();
  ctx.beginPath();
  ctx.arc(center.posX, center.posY, radius, 0, Math.PI * 2);
  ctx.fillStyle = "red";
  ctx.fill();
  ctx.setLineDash([]);
  ctx.lineWidth = 2;
  ctx.strokeStyle = "#B3B1EC";
  ctx.stroke();
  ctx.restore();
}

export function getLeftTopCircleCenter(position: IPoint): IPoint {
  const posX = position.posX ;
  const posY = position.posY;
  return { posX, posY };
}

export function getLeftBottomCircleCenter(
  position: IPoint,
  dimension: IDimension
): IPoint {
  const posX = position.posX;
  const posY = position.posY + dimension.height;
  return { posX, posY };
}

export function getRightTopCircleCenter(
  position: IPoint,
  dimension: IDimension
): IPoint {
  const posX = position.posX + dimension.width;
  const posY = position.posY;
  return { posX, posY };
}

export function getRightBottomCircleCenter(
  position: IPoint,
  dimension: IDimension
): IPoint {
  const posX = position.posX + dimension.width;
  const posY = position.posY + dimension.height;
  return { posX, posY };
}

export function selectionIndicateRectangle(
  ctx: CanvasRenderingContext2D,
  position: IPoint,
  dimension: IDimension,
) {
  ctx.save();
  ctx.beginPath();
  ctx.rect(position.posX, position.posY, dimension.width, dimension.height); 
  ctx.strokeStyle = "gray";
  ctx.lineWidth = 0.1;
  ctx.fillStyle = "transparent";
  ctx.fill(); 
  ctx.stroke(); 
  ctx.restore();
}

let toolbarIcons: NodeListOf<HTMLButtonElement>;
export const body = document.querySelector("body") as HTMLBodyElement;
export const cursorBtn = document.querySelector(
  "#cursorBtn"
) as HTMLButtonElement;
export function highlightCurrentSelectedTool(): void {
  const toolbarContainer = document.querySelector(
    ".toolbar_container"
  ) as HTMLDivElement;
  toolbarIcons = toolbarContainer.querySelectorAll("button");
  toolbarIcons.forEach((tool) => {
    if (tool.id === "cursorBtn") {
      tool.style.backgroundColor = "#E0DFFF";
    }
    tool.addEventListener("click", () => {
      removeBg();
      if (tool.id === "eraserBtn") {
        body.style.cursor =
          "url(https://res.cloudinary.com/drmynjjhg/image/upload/v1719141476/eraser_vnk7nr.png), auto";
      } else if (tool.id === "clearBtn" || tool.id === "cursorBtn") {
        body.style.cursor = "default";
      } else {
        body.style.cursor = "crosshair";
      }
      tool.style.backgroundColor = "#E0DFFF";
    });
  });
}

export function removeBg() {
  toolbarIcons.forEach((tool) => {
    tool.style.backgroundColor = "";
  });
}
